const dataRoles = [];
const dataKeywords = [
  'data science', 'data analyst', 'machine learning', 'analytics', 
  'data engineer', 'business intelligence', 'bi analyst', 
  'statistician', 'quantitative analyst', 'research scientist',
  'ml engineer', 'ai engineer', 'data architect', 'python',
  'sql', 'tableau', 'power bi', 'data mining', 'big data',
  'bigquery', 'spark', 'hadoop', 'etl', 'data warehouse',
  'pandas', 'numpy', 'scikit', 'tensorflow', 'pytorch',
  'snowflake', 'databricks', 'looker', 'qlik', 'sas'
];

for (const item of $input.all()) {
  const title = (item.json.title || '').toLowerCase();
  const description = (item.json.description || '').toLowerCase();
  const company = item.json['dc:creator'] || '';

  const isDataRole = dataKeywords.some(keyword => 
    title.includes(keyword) || description.includes(keyword)
  );

  if (isDataRole && company) {
    // Clean up and organize the data
    item.json.company = company;
    item.json.job_title = item.json.title;
    item.json.job_description = item.json.description;
    item.json.published_date = item.json.pubDate;
    item.json.job_link = item.json.link;

    // Extract all possible info from the RSS description
    const desc = item.json.description || '';

    // Extract location if mentioned
    const locationMatch = desc.match(/(?:Location:|üìç\s*Location:|üìçLocation:|Location\s*:|üåç|üó∫Ô∏è)\s*([^\n\.\,]{1,50})/i);
    if (locationMatch) {
      item.json.location = locationMatch[1].trim().replace(/["\(\)]/g, '');
    }

    // Check for remote work
    const remoteKeywords = ['remote', 'distributed', 'work from home', 'wfh', 'anywhere', 'global', 'worldwide'];
    const isRemote = remoteKeywords.some(keyword => 
      desc.toLowerCase().includes(keyword) || title.toLowerCase().includes('remote')
    );
    item.json.is_remote = isRemote;

    // Extract salary if mentioned
    const salaryRegex = /\$[\d,]+(?:-\$[\d,]+)?|‚Ç¨[\d,]+(?:-‚Ç¨[\d,]+)?|¬£[\d,]+(?:-¬£[\d,]+)?|\d+k\s*-\s*\d+k/gi;
    const salaryMatches = desc.match(salaryRegex);
    if (salaryMatches) {
      item.json.salary_mentioned = salaryMatches.slice(0, 2).join(', ');
    }

    // Extract tech stack
    const techKeywords = [
      'python', 'r', 'sql', 'tableau', 'power bi', 'pandas', 'numpy', 
      'scikit-learn', 'tensorflow', 'pytorch', 'spark', 'hadoop', 
      'aws', 'azure', 'gcp', 'bigquery', 'dataform', 'snowflake', 
      'databricks', 'looker', 'qlik', 'sas', 'spss', 'stata',
      'jupyter', 'docker', 'kubernetes', 'airflow', 'dbt',
      'redshift', 'postgresql', 'mysql', 'mongodb', 'elasticsearch'
    ];
    const mentionedTech = techKeywords.filter(tech => 
      desc.toLowerCase().includes(tech)
    );
    if (mentionedTech.length > 0) {
      item.json.tech_stack = mentionedTech.slice(0, 8).join(', '); // Limit to 8 most relevant
    }

    // Extract company type/industry hints from description
    const industryKeywords = {
      'fintech': ['finance', 'financial', 'banking', 'payment', 'crypto', 'blockchain'],
      'healthcare': ['health', 'medical', 'pharma', 'biotech', 'clinical'],
      'ecommerce': ['ecommerce', 'e-commerce', 'retail', 'marketplace', 'shopping'],
      'saas': ['saas', 'software', 'platform', 'cloud', 'api'],
      'energy': ['energy', 'utility', 'power', 'renewable', 'solar'],
      'media': ['media', 'entertainment', 'streaming', 'content', 'publishing'],
      'logistics': ['logistics', 'supply chain', 'shipping', 'delivery', 'transportation']
    };

    for (const [industry, keywords] of Object.entries(industryKeywords)) {
      if (keywords.some(keyword => desc.toLowerCase().includes(keyword))) {
        item.json.industry_hint = industry;
        break;
      }
    }

    // Extract company size hints
    const sizeHints = {
      'startup': ['startup', 'early stage', 'seed', 'series a'],
      'growth': ['series b', 'series c', 'scale up', 'growing fast'],
      'enterprise': ['enterprise', 'fortune', 'global', 'multinational', 'public company'],
      'unicorn': ['unicorn', 'billion', 'ipo', 'nasdaq', 'nyse']
    };

    for (const [size, keywords] of Object.entries(sizeHints)) {
      if (keywords.some(keyword => desc.toLowerCase().includes(keyword))) {
        item.json.size_hint = size;
        break;
      }
    }

    console.log('‚úÖ Adding data role:', item.json.title);
    console.log('   Company:', company);
    console.log('   Location:', item.json.location || 'Not specified');
    console.log('   Remote:', item.json.is_remote);
    console.log('   Tech:', item.json.tech_stack || 'Not specified');
    console.log('   Industry hint:', item.json.industry_hint || 'Unknown');

    dataRoles.push(item);
  }
}

console.log(`\nüéØ Found ${dataRoles.length} data science/analyst roles`);
return dataRoles;

